/**
 * Prepare Job
 * Validates that a job was found and prepares job metadata for processing.
 * Determines if this is a retry attempt based on retry_count.
 *
 * Queue Selection Logic (from SQL):
 * - Selects jobs with status='pending' OR (status='failed' AND retries remaining)
 * - Limits to 3 concurrent running jobs (MAX_CONCURRENT_JOBS)
 * - Orders by priority (lower=higher) then by creation time (FIFO)
 */

// --- Note: vlmModel comes from job.vlm_model_used or env var via processor ---

// --- Validate Job Exists ---
const jobs = $input.all();
if (!jobs || jobs.length === 0) {
  // No jobs available - return empty to stop workflow gracefully
  return [];
}

// --- Prepare Job Data ---
const job = jobs[0].json;
const isRetry = job.retry_count > 0;

return [{
  json: {
    jobId: job.id,
    receiptId: job.receipt_id,
    createdAt: job.created_at,
    retryCount: job.retry_count,
    maxRetries: job.max_retries,
    isRetry: isRetry,
    processingTime: new Date().toISOString(),
    vlmModel: job.vlm_model_used
  }
}];

/**
 * Log Success
 * Records successful VLM processing with extraction metrics.
 * Includes retry information for monitoring retry effectiveness.
 */
const result = $input.first().json;
const timestamp = new Date().toISOString();
const jobInfo = $node['Prepare Job'].json;

console.log(`[Queue Monitor] Processing succeeded:`, {
  timestamp,
  jobId: result.jobId,
  receiptId: result.receiptId,
  itemsExtracted: result.itemsExtracted,
  totalConfidence: result.totalConfidence,
  model: result.model,
  isRetry: jobInfo.isRetry,
  retryCount: jobInfo.retryCount
});

return [{ 
  json: { 
    processed: true, 
    timestamp, 
    result: result
  } 
}];

/**
 * Log Failure
 * Records VLM processing failure with error details.
 * Tracks retry status for monitoring automatic recovery.
 */
const result = $input.first().json;
const timestamp = new Date().toISOString();
const jobInfo = $node['Prepare Job'].json;

console.error(`[Queue Monitor] Processing failed:`, {
  timestamp,
  statusCode: result.status_code,
  error: result?.error || result?.message || 'Unknown error',
  jobId: result?.jobId || jobInfo.jobId,
  receiptId: result?.receiptId || jobInfo.receiptId,
  isRetry: jobInfo.isRetry,
  retryCount: jobInfo.retryCount,
  maxRetries: jobInfo.maxRetries,
  retriesRemaining: jobInfo.maxRetries - jobInfo.retryCount - 1
});

return [{ 
  json: { 
    error: true, 
    timestamp, 
    message: 'Receipt processing failed',
    statusCode: result.status_code,
    details: result
  } 
}];

/**
 * Error Handler
 * Catches and logs errors from any node in the workflow.
 * These are infrastructure errors (DB failures, network issues), not VLM processing failures.
 */
const error = $input.first();
const timestamp = new Date().toISOString();

console.error(`[Queue Monitor] Infrastructure error:`, {
  timestamp,
  error: error.json?.error || error.message || 'Unknown error',
  node: error.json?.node || 'Unknown node',
  jobId: error.json?.jobId || 'Unknown'
});

return [{ 
  json: { 
    error: true, 
    timestamp, 
    message: 'Queue processing failed',
    details: error.json || error.message 
  } 
}];
